INSERT INTO `t_content` VALUES ('8', '117', '南昌联发广场', '', '', '', null, '<p><strong>南昌联发广场</strong>为毗邻市政府的临江项目，位于行政金融中心核心区，占地47亩，总建筑面积约为21万平方米，主体建筑超200米，总投资10亿元人民币。作为南昌城市地标建筑，联发广场项目是集5A级摩天写字楼、时尚购物中心、高档奢装公寓为一体的大型综合体。</p>', null, '2016-08-17 14:47:10', '1', '2016-10-31 15:28:19', '1', '0', null, null, null);
