INSERT INTO `t_content` VALUES ('78', '298', '会展北商务综合体', '', '', '', null, '<p>厦门会展北项目位于厦门环岛路与吕岭路交叉口西南侧。所在的会展片区位于厦门岛东部，属厦门政府重点打造的“海西金融中心区域”。该项目总投资约30亿元，总用地面积4万多平方米，建筑面积21.5万平方米，项目规划有天虹在海西旗舰店天虹君尚购物中心、五星级酒店、新天地商业街、福建省内唯一获得美国LEED金级和国家绿色建筑三星双认证的，两栋超高层全海景5A甲级写字楼，集商业、办公、酒店为一体，建成后将会成为厦门地标性高端商务综合体。<br></p>', null, '2016-10-31 15:53:40', '1', '2016-10-31 16:38:06', '1', '0', null, null, null);
