INSERT INTO `t_web_develop_history` VALUES ('35', '257', '汪名川先生', '', '汪名川先生，中国国籍，1966年12月出生，高级会计师，同济大学管理工程硕士，中欧国际工商学院EMBA，无境外永久居留权。历任成都航空发动机公司财务处财务主管、深圳深蓉工程塑料公司财务部经理、中国航空技术深圳有限公司财务部副经理，此期间兼任深圳中航商贸公司综合管理部经理和财务总监、中国航空技术深圳有限公司财务审计部经理，现任中国航空技术国际控股有限公司副总会计师、中国航空技术深圳有限公司总会计师、飞亚达（集团）股份有限公司董事、天马微电子股份有限公司董事、中航地产股份有限公司董事，本公司董事。', null, '2016-11-02 15:02:43', '1', null, null, '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('36', '257', '李世佳先生', '', '李世佳先生，中国国籍，香港永久性居民，1965年7月出生，大学本科学历，香港会计师。历任香港上市公司中国食品有限公司财务总监、香港上市公司祥泰行集团有限公司（现称德祥地产集团有限公司）执行董事兼公司秘书、莱蒙国际集团有限公司执行董事，现任莱蒙国际集团有限公司非执行董事、五龙贸易有限公司董事，本公司董事。', null, '2016-11-02 15:03:08', '1', null, null, '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('37', '257', '钟思均先生', '', '钟思均先生，中国国籍，1976年12月出生，西北工业大学管理学硕士，高级经济师，无境外永久居留权。历任上海天马微电子有限公司董事会秘书、中航国际控股股份有限公司经营管理部副经理等职务，现任中航国际控股股份有限公司董事会秘书、飞亚达（集团）股份有限公司董事、中航地产股份有限公司董事、天马微电子股份有限公司董事，本公司董事。', null, '2016-11-02 15:03:38', '1', null, null, '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('38', '257', '涂成洲先生', '', '涂成洲先生，中国国籍，1968年10月出生，本科学历，律师，无境外永久居留权。曾先后任江西金溪税务局干部、深圳华强集团科长、广东维科特律师事务所合伙人、广东金唐律师事务所合伙人、深圳建筑协会专家、深圳律师协会证券基金期货法律业务委员会副主任，现任大成律师事务所合伙人、深圳市雷赛智能控制股份有限公司独立董事、深圳市锐明技术股份有限公司独立董事、深圳种子资本有限公司执行董事和法定代表人、深圳名德企业管理中心（有限合伙）执行合伙人，兼任中国社会科学院法学研究所法律硕士实务指导教授、中山大学管理学院校外导师，本公司独立董事。', null, '2016-11-02 15:03:59', '1', null, null, '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('39', '257', '欧伟明先生', '', '欧伟明先生，中国国籍，香港永久性居民，具有澳大利亚居留权，1946年11月出生，大学本科学历。曾先后任广东惠州市造船厂副厂长、广东惠州市经委生产组长、广东省经济委员会科长和处长、广东省烟草公司副经理、粤海投资有限公司董事副总经理、和记黄埔广州地产公司总经理、香港明丰集团公司总裁，现任盈利时控股有限公司非执行董事，本公司独立董事。', null, '2016-11-02 15:04:25', '1', null, null, '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('40', '257', '叶少琴女士', '', '叶少琴女士，中国国籍，1965年9月出生，厦门大学会计学博士，无境外永久居留权。历任厦门大学经济学院会计系助教、讲师和副教授、福建龙溪轴承股份有限公司独立董事，现任厦门大学管理学院会计系教授、漳州片仔癀药业股份有限公司独立董事、银基烯碳新材料股份有限公司独立董事、福建浔兴拉链科技股份有限公司独立董事、厦门艾德生物医药科技股份有限公司独立董事、福州瑞芯微电子股份有限公司独立董事、华福基金管理有限责任公司独立董事，本公司独立董事。', null, '2016-11-02 15:04:47', '1', null, null, '0', null, null, null);
