INSERT INTO `t_web_develop_history` VALUES ('66', '254', '公司法', '', '公司法是规定各类公司的设立、活动、解散及其他对外关系的法律规范的总称，是市场的主体法。其意义：鼓励投资创业；强化公司的意思自治；加强对债人的保护；加强对中小股东利益的保护；强化公司社会责任和职工保护措施。\n\n1993年12月29日第八届全国人民代表大会常务委员会第五次会议通过 ，1999年、2004年、 2005年多次修正，现行版本由全国人民代表大会常务委员会于2013年12月28日发布。', null, '2016-11-14 15:38:40', '1', null, null, '0', '', null, null);
