INSERT INTO `t_web_develop_history` VALUES ('87', '273', '天虹会员卡、储值卡在餐饮商户、玩乐商户可以使用吗？', '', '回答：天虹会员卡、储值卡部分餐饮商户、玩乐商户可以使用，具体可咨询购物中心客服中心，或见商户店内标识。', '2016-11-16 09:40:04', '2016-11-15 16:07:08', '1', '2016-11-16 09:40:04', '1', '0', '', null, null);
INSERT INTO `t_web_develop_history` VALUES ('88', '273', '如何才能取得停车优惠券？', '', '回答：可凭购物小票、会员积分前往购物中心客服中心兑换相应停车优惠，吉安天虹购物中心已实现智能停车服务，可在天虹虹领巾平台上领取停车优惠，并支付停车费。', '2016-11-16 09:40:13', '2016-11-15 16:07:39', '1', '2016-11-16 09:40:13', '1', '0', '', null, null);
