INSERT INTO `t_web_develop_history` VALUES ('145', '309', '侯毅女士', '', '侯毅女士，中国国籍，1964年4月出生，大学本科学历，中欧国际工商学院EMBA，经济师，无境外永久居留权。曾任职于长春针纺织品公司及深圳东方天虹商场，历任公司深南天虹商场总经理、统配中心经理，公司总经理助理兼营运管理部经理，现任深圳市康达特投资咨询有限公司监事，深圳市奥尔投资发展有限公司监事，深圳市奥轩投资股份有限公司监事，本公司副总经理。', '2016-12-22 13:53:50', '2016-11-17 10:07:01', '1', '2016-12-22 13:53:50', '1', '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('146', '309', '吴健琼女士', '', '吴健琼女士，中国国籍，1967年8月出生，大学本科学历，中欧国际工商学院EMBA，高级经济师，无境外永久居留权。历任南昌建设银行、中国银行文员，公司总经理秘书，统配中心经理、福民、深南天虹商场、天虹购物广场总经理，营运部经理，公司总经理助理，现任深圳市奥尔投资发展有限公司监事，深圳市可来投资咨询有限公司监事，深圳市奥轩投资股份有限公司监事，本公司副总经理。', '2016-12-22 13:53:46', '2016-11-17 10:07:20', '1', '2016-12-22 13:53:46', '1', '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('147', '309', '孙金成先生', '', '孙金成先生，中国国籍，1964年7月出生，大学本科学历，中欧国际工商学院EMBA，经济师，无境外永久居留权。历任河南省粮食学校教师，天津百货采购供应站劳保用品公司副经理，深南天虹商场分部经理、部门经理助理，公司统配中心经理，深南天虹商场、创业天虹商场、常兴天虹商场总经理，公司总经理助理，现任深圳市康达特投资咨询有限公司执行董事，深圳市奥轩投资股份有限公司监事，本公司副总经理。', '2016-12-22 13:53:42', '2016-11-17 10:07:40', '1', '2016-12-22 13:53:42', '1', '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('148', '309', '郑蔓女士', '', '郑蔓女士，中国国籍，1971年4月出生，大学本科学历，经济师，无境外永久居留权。历任东门天虹副总经理、公司营运管理部副经理、梅林天虹总经理、营运部经理、战略部经理、华南区副总经理、战略与营运总监、电商事业部总经理、助理总经理，现任公司副总经理。', '2016-12-22 13:53:35', '2016-11-17 10:07:57', '1', '2016-12-22 13:53:35', '1', '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('149', '309', '姜勇先生', '', '姜勇先生，中国国籍，1965年12月出生，硕士研究生学历，北京大学光华管理学院EMBA，经济师，无境外永久居留权。历任深圳天丽光学眼镜厂业务经理，公司采购经理、招商部经理助理、厦门汇腾天虹副总经理、深圳沙井天虹总经理、天虹购物广场总经理、华中区总经理、商品中心总监、公司助理总经理，现任公司副总经理。', '2016-12-22 13:53:28', '2016-11-17 10:08:15', '1', '2016-12-22 13:53:28', '1', '0', null, null, null);
INSERT INTO `t_web_develop_history` VALUES ('150', '309', '张旭华先生', '', '张旭华先生，中国国籍，1977年3月出生，硕士研究生学历，无境外永久居留权。历任万科工业有限公司市场部职员、天虹招商部部长、采购部经理、君尚百货总经理、商品中心经理、成都公司总经理、公司助理总经理、购物中心事业部总经理，现任公司副总经理。', '2016-12-22 13:53:24', '2016-11-17 10:08:35', '1', '2016-12-22 13:53:24', '1', '0', null, null, null);
