			AND backup1 = #{backup1,jdbcType=VARCHAR}
		
		
			AND backup2 = #{backup2,jdbcType=VARCHAR}
		
		
			AND backup3 = #{backup3,jdbcType=VARCHAR}
		
		
			AND introduce = #{introduce,jdbcType=LONGVARCHAR}

