mapper.select:
select
from t_store_pre_buy where is_deleted = 0
AND category_id = #{categoryId,jdbcType=INTEGER}
AND name like CONCAT(#{name,jdbcType=VARCHAR},'%' )
ORDER BY create_time LIMIT #{start, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}

select count(1) from t_store_pre_buy where is_deleted = 0
AND category_id = #{categoryId,jdbcType=INTEGER}
AND name like CONCAT(#{name,jdbcType=VARCHAR},'%' )
