mapper.insert:
insert into t_sys_role (id, code, role_name, 
  role_description, create_time, create_id, 
  update_time, update_id, is_deleted, 
  backup1, backup2, backup3
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, 
  #{roleDescription,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createId,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateId,jdbcType=INTEGER}, #{isDeleted,jdbcType=BIT}, 
  #{backup1,jdbcType=VARCHAR}, #{backup2,jdbcType=VARCHAR}, #{backup3,jdbcType=VARCHAR}
  )

mapper.select:
    select 
    
    from t_sys_role
    where is_deleted = 0
    	
	        AND role_name = #{roleName,jdbcType=VARCHAR}
	    
		
	        AND create_time BETWEEN #{startDate,jdbcType=VARCHAR} AND #{endDate,jdbcType=VARCHAR}
		
    ORDER BY create_time desc
    LIMIT #{start, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}

    select 
    	count(1)
    from t_sys_role
    where  is_deleted = 0
	    
	        AND role_name = #{roleName,jdbcType=VARCHAR}
	    
		
	        AND create_time BETWEEN #{startDate,jdbcType=VARCHAR} AND #{endDate,jdbcType=VARCHAR}
